niqueIds\";b:0;s:9:\"\0*\0hidden\";a:0:{}s:10:\"\0*\0visible\";a:0:{}s:11:\"\0*\0fillable\";a:4:{i:0;s:4:\"name\";i:1;s:4:\"slug\";i:2;s:11:\"description\";i:3;s:4:\"icon\";}s:10:\"\0*\0guarded\";a:1:{i:0;s:1:\"*\";}s:16:\"\0*\0forceDeleting\";b:0;}s:6:\"author\";O:17:\"App\\Models\\Author\":33:{s:13:\"\0*\0connection\";s:5:\"mysql\";s:8:\"\0*\0table\";s:7:\"authors\";s:13:\"\0*\0primaryKey\";s:2:\"id\";s:10:\"\0*\0keyType\";s:3:\"int\";s:12:\"incrementing\";b:1;s:7:\"\0*\0with\";a:0:{}s:12:\"\0*\0withCount\";a:0:{}s:19:\"preventsLazyLoading\";b:0;s:10:\"\0*\0perPage\";i:15;s:6:\"exists\";b:1;s:18:\"wasRecentlyCreated\";b:0;s:28:\"\0*\0escapeWhenCastingToString\";b:0;s:13:\"\0*\0attributes\";a:9:{s:2:\"id\";i:3;s:4:\"name\";s:13:\"Iryana Wihtee\";s:5:\"email\";s:16:\"iryana@gmail.com\";s:3:\"bio\";s:8:\"private.\";s:10:\"created_at\";s:19:\"2025-06-16 18:31:50\";s:10:\"updated_at\";s:19:\"2025-06-16 18:31:50\";s:9:\"is_active\";i:1;s:5:\"photo\";s:30:\"01JXW852YHRADYPNA6E5SNBGDM.png\";s:7:\"user_id\";i:4;}s:11:\"\0*\0original\";a:9:{s:2:\"id\";i:3;s:4:\"name\";s:13:\"Iryana Wihtee\";s:5:\"email\";s:16:\"iryana@gmail.com\";s:3:\"bio\";s:8:\"private.\";s:10:\"created_at\";s:19:\"2025-06-16 18:31:50\";s:10:\"updated_at\";s:19:\"2025-06-16 18:31:50\";s:9:\"is_active\";i:1;s:5:\"photo\";s:30:\"01JXW852YHRADYPNA6E5SNBGDM.png\";s:7:\"user_id\";i:4;}s:10:\"\0*\0changes\";a:0:{}s:11:\"\0*\0previous\";a:0:{}s:8:\"\0*\0casts\";a:0:{}s:17:\"\0*\0classCastCache\";a:0:{}s:21:\"\0*\0attributeCastCache\";a:0:{}s:13:\"\0*\0dateFormat\";N;s:10:\"\0*\0appends\";a:0:{}s:19:\"\0*\0dispatchesEvents\";a:0:{}s:14:\"\0*\0observables\";a:0:{}s:12:\"\0*\0relations\";a:0:{}s:10:\"\0*\0touches\";a:0:{}s:27:\"\0*\0relationAutoloadCallback\";N;s:26:\"\0*\0relationAutoloadContext\";N;s:10:\"timestamps\";b:1;s:13:\"usesUniqueIds\";b:0;s:9:\"\0*\0hidden\";a:0:{}s:10:\"\0*\0visible\";a:0:{}s:11:\"\0*\0fillable\";a:6:{i:0;s:4:\"name\";i:1;s:5:\"email\";i:2;s:3:\"bio\";i:3;s:5:\"photo\";i:4;s:9:\"is_active\";i:5;s:7:\"user_id\";}s:10:\"\0*\0guarded\";a:1:{i:0;s:1:\"*\";}}}s:10:\"\0*\0touches\";a:0:{}s:27:\"\0*\0relationAutoloadCallback\";N;s:26:\"\0*\0relationAutoloadContext\";N;s:10:\"timestamps\";b:1;s:13:\"usesUniqueIds\";b:0;s:9:\"\0*\0hidden\";a:0:{}s:10:\"\0*\0visible\";a:0:{}s:11:\"\0*\0fillable\";a:12:{i:0;s:5:\"title\";i:1;s:7:\"content\";i:2;s:9:\"thumbnail\";i:3;s:6:\"status\";i:4;s:11:\"category_id\";i:5;s:9:\"author_id\";i:6;s:12:\"published_at\";i:7;s:5:\"views\";i:8;s:6:\"is_hot\";i:9;s:11:\"is_headline\";i:10;s:11:\"is_featured\";i:11;s:16:\"is_editor_choice\";}s:10:\"\0*\0guarded\";a:1:{i:0;s:1:\"*\";}s:16:\"\0*\0forceDeleting\";b:0;}i:1;O:18:\"App\\Models\\Article\":34:{s:13:\"\0*\0connection\";s:5:\"mysql\";s:8:\"\0*\0table\";s:8:\"articles\";s:13:\"\0*\0primaryKey\";s:2:\"id\";s:10:\"\0*\0keyType\";s:3:\"int\";s:12:\"incrementing\";b:1;s:7:\"\0*\0with\";a:0:{}s:12:\"\0*\0withCount\";a:0:{}s:19:\"preventsLazyLoading\";b:0;s:10:\"\0*\0perPage\";i:15;s:6:\"exists\";b:1;s:18:\"wasRecentlyCreated\";b:0;s:28:\"\0*\0escapeWhenCastingToString\";b:0;s:13:\"\0*\0attributes\";a:17:{s:2:\"id\";i:15;s:5:\"title\";s:111:\"Penguatan Spiritual Lewat Ibadah Minggu, Rutan Balikpapan Fasilitasi Pembinaan Kepribadian Warga Binaan Nasrani\";s:7:\"content\";s:2117:\"<h3>Pembinaan Spiritual di Rutan Balikpapan: Ibadah Minggu sebagai Sumber Ketenangan Jiwa</h3><p><strong>BALIKPAPAN</strong> – Menjaga ketenangan jiwa dan harapan hidup di balik jeruji besi bukanlah hal mudah, namun bagi warga binaan di Rutan Kelas IIA Balikpapan, kekuatan iman menjadi salah satu jawabannya. Pada Minggu, 15 Juni 2025, Rutan Kelas IIA Balikpapan kembali menyelenggarakan ibadah Minggu bagi warga binaan beragama Nasrani, sebagai bagian integral dari program pembinaan kepribadian yang humanis.</p><p><br></p><h3>Dua Sesi Ibadah Penuh Harapan</h3><p>Kegiatan ibadah ini dilaksanakan dalam dua sesi di aula utama Rutan dan diikuti dengan penuh semangat oleh para warga binaan.</p><p>Ibadah pagi dimulai pukul 10.00 WITA, dipimpin oleh <strong>Pendeta Jori dari Gereja HGC Balikpapan</strong>. Dalam khotbah singkatnya, Pendeta Jori menyampaikan pesan inspiratif: \"Kenaikan dan kemuliaan Tuhan Yesus adalah pengingat bahwa setiap penderitaan di dunia adalah sementara. Percayalah, setiap orang yang berseru pada-Nya tak pernah ditinggalkan.\"</p><p>Selanjutnya, ibadah sore pukul 14.00 WITA dipimpin oleh <strong>Penatua Meidy Mailangkay Pungus dari GPIB Bukit Sion Balikpapan</strong>. Beliau menyampaikan pesan pengharapan yang menyentuh: \"Tuhan menanamkan kasih bahkan di tempat yang penuh batas. Tidak ada yang terlalu jauh dari jangkauan kasih-Nya, termasuk di tempat ini.\"</p><p><br></p><h3>Komitmen Rutan dalam Pembinaan Spiritual</h3><p>Kegiatan ini berlangsung khidmat, menunjukkan komitmen Rutan Balikpapan dalam berkolaborasi dengan gereja-gereja lokal sebagai bentuk nyata sinergi pembinaan spiritual yang berkelanjutan.</p><p><strong>Kepala Rutan Balikpapan, Bapak Agus Salim</strong>, menjelaskan pentingnya program ini. \"Kami percaya bahwa pembinaan spiritual adalah salah satu kunci pemulihan diri. Dengan semangat keimanan, warga binaan dapat memaknai hidup dengan lebih baik dan menata masa depan yang lebih positif,\" ujarnya.</p><p>Rutan Balikpapan berkomitmen untuk terus mendukung kegiatan keagamaan sebagai bagian dari upaya pemasyarakatan yang humanis dan berkeadilan.&nbsp;</p>\";s:9:\"thumbnail\";s:30:\"01JXW8EF20GQCV085JCDA56THQ.jpg\";s:6:\"status\";s:9:\"published\";s:5:\"views\";i:0;s:11:\"category_id\";i:14;s:9:\"author_id\";i:3;s:12:\"published_at\";s:19:\"2025-06-16 00:00:00\";s:10:\"created_at\";s:19:\"2025-06-16 18:36:58\";s:10:\"updated_at\";s:19:\"2025-06-16 18:36:58\";s:10:\"deleted_at\";N;s:6:\"is_hot\";i:0;s:11:\"is_headline\";i:0;s:11:\"is_featured\";i:1;s:16:\"is_editor_choice\";i:0;s:14:\"comments_count\";i:0;}s:11:\"\0*\0original\";a:17:{s:2:\"id\";i:15;s:5:\"title\";s:111:\"Penguatan Spiritual Lewat Ibadah Minggu, Rutan Balikpapan Fasilit