uches\";a:0:{}s:27:\"\0*\0relationAutoloadCallback\";N;s:26:\"\0*\0relationAutoloadContext\";N;s:10:\"timestamps\";b:1;s:13:\"usesUniqueIds\";b:0;s:9:\"\0*\0hidden\";a:0:{}s:10:\"\0*\0visible\";a:0:{}s:11:\"\0*\0fillable\";a:12:{i:0;s:5:\"title\";i:1;s:7:\"content\";i:2;s:9:\"thumbnail\";i:3;s:6:\"status\";i:4;s:11:\"category_id\";i:5;s:9:\"author_id\";i:6;s:12:\"published_at\";i:7;s:5:\"views\";i:8;s:6:\"is_hot\";i:9;s:11:\"is_headline\";i:10;s:11:\"is_featured\";i:11;s:16:\"is_editor_choice\";}s:10:\"\0*\0guarded\";a:1:{i:0;s:1:\"*\";}s:16:\"\0*\0forceDeleting\";b:0;}}s:28:\"\0*\0escapeWhenCastingToString\";b:0;}', 1750527417);
